import React,{useEffect, useState} from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Button, Container,Paper } from '@mui/material';
import Alert from '@mui/material/Alert';
import Stack from '@mui/material/Stack';
import { padding } from '@mui/system';

export default function Student() {
    const [name,setName]=useState('')
    const [address,setAddress]=useState('')
    const[students,setStudents]=useState([])
    const sub=(e)=>
    {
        e.preventDefault();
        const student={name,address}
        console.log(student);
        fetch("http://localhost:8080/student/add",{
        method:"POST",
        headers:{"Content-Type":"application/json"},
        body:JSON.stringify(student)}).then(()=>{<Alert severity="error">Done</Alert>})
       
    }
    useEffect(()=>{
        fetch("http://localhost:8080/student/getAll")
        .then(res=>res.json())
        .then((result)=>
        {
            setStudents(result);
        }
        )
    },[])

    
  return (
      <div className='we'>
      <Container>

    <Box 
      component="form"
      sx={{
        '& > :not(style)': { m: 1 },
      }}
      noValidate
      autoComplete="off"
    >
        <h1>Student DB</h1>
      <TextField id="standard-basic" label="Student Name" variant="outlined" fullWidth value={name} onChange={(e)=>setName(e.target.value)} />
      <br></br>
      <TextField id="standard-basic" label="Student Address" variant="outlined" fullWidth  value={address} onChange={(e)=>setAddress(e.target.value)}/>
      <br></br>
      <Button variant="contained" onClick={sub}>Submit</Button>
      {name}
      {address}
    </Box>
    <br>
    </br>
    <br>
    </br>
    <br>
    </br>
    <br>
    </br>
    <br>
    </br>
    
    <div className="er">
        
        {students.map(student=>(
            <p>
            Id:{student.id}
            Name:{student.name}
            Address:{student.address}
            </p>
        ))}
    </div>

    </Container>
    </div>
    
  );
}
